import { NextResponse } from "next/server";
import type { NextRequest } from "next/server";
import { apiExternal } from "@/lib/apiExternal";

/**
 * Faz a chamada para a API externa para obter um projeto por id.
 * Retorna a resposta externa (status/e body) ou um JSON com mensagem de erro.
 */

export const runtime = "nodejs";
export const dynamic = "force-dynamic";

function handleApiError(error: any) {
  const status = error?.response?.status || 500;
  const message =
    error?.response?.data?.message || "Erro na comunicação com a API externa.";
  return NextResponse.json({ message }, { status });
}

export async function GET(request: Request, { params }: { params: { id: string } }) {
  const { id } = params;

  try {
    // apiExternal tem baseURL configurada (NEST_API_URL) e o interceptor pega cookie "auth-token"
    const resp = await apiExternal.get(`/projects/${id}`);

    // Normalmente resp.data já contém a estrutura da API externa.
    // Retornamos status original e body direto para manter consistência.
    return NextResponse.json(resp.data, { status: resp.status });
  } catch (error) {
    console.error("[PROJECT_ID_GET]", error);
    return handleApiError(error);
  }
}
