## 4. Atualizar `createResourceHook.ts` para suportar Query Params

```typescript
// src/hooks/createResourceHook.ts
import { useCallback, useEffect, useRef, useState } from "react";
import { apiClient } from "@/lib/apiClient";

type ResponseDataApi<T> = { 
  success: boolean;
  message: string;
  data: T; 
};

type ReturnCreateResourceHook<TData, TCreate, TUpdate> = {
  (filters?: Record<string, any>): {
    data: TData[] | null;
    loading: boolean;
    error: string | null;
    meta?: any; // Para paginação: { total, page, pageSize }
    actions: {
      load: () => Promise<TData[]>;
      create: (payload: TCreate) => Promise<TData>;
      update: (id: string, payload: TUpdate) => Promise<TData>;
      remove: (id: string) => Promise<void>;
    };
  };
  (id: string, filters?: Record<string, any>): {
    data: TData | null;
    loading: boolean;
    error: string | null;
    actions: {
      load: () => Promise<TData>;
      create: (payload: TCreate) => Promise<TData>;
      update: (payload: TUpdate) => Promise<TData>;
      remove: () => Promise<void>;
    };
  };
};

function toMessage(err: unknown): string {
  if (err && typeof err === "object") {
    const anyErr = err as any;
    return (
      anyErr?.response?.data?.error ||
      anyErr?.response?.data?.message ||
      anyErr?.message ||
      "Erro inesperado"
    );
  }
  return String(err) || "Erro inesperado";
}

export function createResourceHook<TData, TCreate = any, TUpdate = any>(
  baseEndpoint: string
): ReturnCreateResourceHook<TData, TCreate, TUpdate> {
  return function useResource(idOrFilters?: string | Record<string, any>, filtersWhenId?: Record<string, any>) {
    // Determina se é um ID ou filtros
    const isId = typeof idOrFilters === 'string';
    const id = isId ? idOrFilters : undefined;
    const filters = isId ? filtersWhenId : idOrFilters;
    const isList = !id;

    const endpoint = id ? `${baseEndpoint}/${id}` : baseEndpoint;

    const [data, setData] = useState<TData | TData[] | null>(null);
    const [loading, setLoading] = useState(false);
    const [error, setError] = useState<string | null>(null);
    const [meta, setMeta] = useState<any>(null);
    const abortRef = useRef<AbortController | null>(null);

    const load = useCallback(async (): Promise<TData | TData[]> => {
      if (!endpoint) throw new Error("Endpoint não definido");

      setLoading(true);
      setError(null);
      abortRef.current?.abort();
      abortRef.current = new AbortController();

      try {
        // Adiciona query params se houver filtros
        let url = endpoint;
        if (filters && Object.keys(filters).length > 0) {
          const params = new URLSearchParams();
          Object.entries(filters).forEach(([key, value]) => {
            if (value !== undefined && value !== null && value !== '') {
              params.set(key, String(value));
            }
          });
          const queryString = params.toString();
          if (queryString) {
            url = `${endpoint}?${queryString}`;
          }
        }

        const res = await apiClient.get<ResponseDataApi<any>>(url, {
          signal: abortRef.current.signal,
        });

        // A API NestJS retorna: { success: true, message: "...", data: { projects: [...], total: N } }
        // ou { success: true, message: "...", data: { project: {...} } }

        if (isList) {
          // Para lista: res.data.data.projects
          const projects = res.data.data.projects || res.data.data || [];
          setData(projects);
          setMeta({
            total: res.data.data.total,
            page: res.data.data.page,
            pageSize: res.data.data.pageSize,
          });
          return projects;
        } else {
          // Para item único: res.data.data.project
          const project = res.data.data.project || res.data.data;
          setData(project);
          return project;
        }
      } catch (err: any) {
        if (err.name !== "AbortError") {
          setError(toMessage(err));
        }
        throw err;
      } finally {
        setLoading(false);
      }
    }, [endpoint, filters, isList]);

    const create = useCallback(
      async (payload: TCreate): Promise<TData> => {
        setLoading(true);
        setError(null);

        try {
          const res = await apiClient.post<ResponseDataApi<any>>(
            baseEndpoint,
            payload
          );
          await load(); // Recarrega após criação
          return res.data.data.project || res.data.data;
        } catch (err) {
          setError(toMessage(err));
          throw err;
        } finally {
          setLoading(false);
        }
      },
      [baseEndpoint, load]
    );

    const update = useCallback(
      async (idOrPayload: string | TUpdate, payloadIfId?: TUpdate): Promise<TData> => {
        // Aceita tanto update(id, payload) quanto update(payload) quando já tem id
        const updateId = typeof idOrPayload === 'string' ? idOrPayload : id;
        const updatePayload = typeof idOrPayload === 'string' ? payloadIfId! : idOrPayload;

        if (!updateId) throw new Error("ID não fornecido para update");

        setLoading(true);
        setError(null);

        try {
          const res = await apiClient.put<ResponseDataApi<any>>(
            `${baseEndpoint}/${updateId}`,
            updatePayload
          );
          await load(); // Recarrega após update
          return res.data.data.project || res.data.data;
        } catch (err) {
          setError(toMessage(err));
          throw err;
        } finally {
          setLoading(false);
        }
      },
      [baseEndpoint, id, load]
    );

    const remove = useCallback(async (removeId?: string): Promise<void> => {
      const deleteId = removeId || id;
      if (!deleteId) throw new Error("ID não fornecido para remove");

      setLoading(true);
      setError(null);

      try {
        await apiClient.delete(`${baseEndpoint}/${deleteId}`);
        await load(); // Recarrega após remoção
      } catch (err) {
        setError(toMessage(err));
        throw err;
      } finally {
        setLoading(false);
      }
    }, [baseEndpoint, id, load]);

    useEffect(() => {
      if (endpoint) {
        load();
      }

      return () => {
        abortRef.current?.abort();
      };
    }, [load, endpoint]);

    if (isList) {
      return {
        data: data as TData[] | null,
        loading,
        error,
        meta,
        actions: {
          load: load as () => Promise<TData[]>,
          create,
          update,
          remove,
        },
      };
    } else {
      return {
        data: data as TData | null,
        loading,
        error,
        actions: {
          load: load as () => Promise<TData>,
          create,
          update: update as (payload: TUpdate) => Promise<TData>,
          remove: remove as () => Promise<void>,
        },
      };
    }
  } as ReturnCreateResourceHook<TData, TCreate, TUpdate>;
}
