```typescript
// src/hooks/createResourceHook.ts (versão simplificada)
import { useCallback, useEffect, useRef, useState } from "react";
import { apiClient } from "@/lib/apiClient";

type ResponseDataApi<T> = { 
  success: boolean;
  message: string;
  data: T; 
};

export function createResourceHook<TData = any, TCreate = any, TUpdate = any>(
  baseEndpoint: string
) {
  return function useResource(id?: string) {
    const endpoint = id ? `${baseEndpoint}/${id}` : baseEndpoint;

    const [data, setData] = useState<TData | null>(null);
    const [loading, setLoading] = useState(false);
    const [error, setError] = useState<string | null>(null);
    const abortRef = useRef<AbortController | null>(null);

    // load agora aceita query params opcionais
    const load = useCallback(async (queryParams?: Record<string, any>): Promise<TData> => {
      setLoading(true);
      setError(null);
      abortRef.current?.abort();
      abortRef.current = new AbortController();

      try {
        // Constrói a URL com query params se existirem
        let url = endpoint;
        if (queryParams && Object.keys(queryParams).length > 0) {
          const params = new URLSearchParams();
          Object.entries(queryParams).forEach(([key, value]) => {
            if (value !== undefined && value !== null && value !== '') {
              params.set(key, String(value));
            }
          });
          const queryString = params.toString();
          if (queryString) {
            url = `${endpoint}?${queryString}`;
          }
        }

        // Faz a requisição com axios (apiClient)
        const res = await apiClient.get<ResponseDataApi<TData>>(url, {
          signal: abortRef.current.signal,
