{"explanation": "Atualizando o api-client para incluir todos os métodos HTTP necessários", "filePath": "/home/monteiro/codes/vitaport_front/lib/api-client.ts", "code": "import axios from "axios";

export class APIClient {
  private baseURL = "http://localhost:3000";
  private token: string | null = null;

  constructor() {
    if (typeof window !== "undefined") {
      this.token = localStorage.getItem("token");
    }
  }

  private getHeaders() {
    return {
      Authorization: this.token ? `Bearer ${this.token}` : "",
      "Content-Type": "application/json",
    };
  }

  setToken(token: string) {
    this.token = token;
    if (typeof window !== "undefined") {
      localStorage.setItem("token", token);
    }
  }

  clearToken() {
    this.token = null;
    if (typeof window !== "undefined") {
      localStorage.removeItem("token");
    }
  }

  async get<T>(path: string): Promise<{ data: T }> {
    const response = await axios.get(`${this.baseURL}${path}`, {
      headers: this.getHeaders(),
    });
    return { data: response.data };
  }

  async post<T>(path: string, data: any): Promise<{ data: T }> {
    const response = await axios.post(`${this.baseURL}${path}`, data, {
      headers: this.getHeaders(),
    });
    return { data: response.data };
  }
}

export const apiClient = new APIClient();"}

