{"explanation": "Atualizando o api-client para usar a URL base correta e melhorar o gerenciamento do token", "filePath": "/home/monteiro/codes/vitaport_front/lib/api-client.ts", "code": "import axios from "axios";
import { toast } from "sonner";

export const handleAPIError = (error: any) => {
  console.error("API Error: ", error);
  const message = error.response?.data?.message || error.message || "An error occurred";
  toast.error(message);
  if (error.response?.status === 401 || error.response?.status === 403) {
    // Token expirado ou inválido
    localStorage.removeItem("token");
    window.location.href = "/login";
  }
};

export class APIClient {
  private baseURL = "http://localhost:8081"; // URL base correta
  private token: string | null = null;

  constructor() {
    if (typeof window !== "undefined") {
      this.token = localStorage.getItem("token");
    }
  }

  private getHeaders() {
    const headers: Record<string, string> = {
      "Content-Type": "application/json",
    };

    if (this.token) {
      headers["Authorization"] = `Bearer ${this.token}`;
    }

    return headers;
  }

  setToken(token: string) {
    this.token = token;
    if (typeof window !== "undefined") {
      localStorage.setItem("token", token);
    }
  }

  clearToken() {
    this.token = null;
    if (typeof window !== "undefined") {
      localStorage.removeItem("token");
    }
  }

  async get<T>(path: string): Promise<{ data: T }> {
    try {
      const response = await axios.get(`${this.baseURL}${path}`, {
        headers: this.getHeaders(),
      });
      return { data: response.data };
    } catch (error) {
      handleAPIError(error);
      throw error;
    }
  }

  async post<T>(path: string, data: any): Promise<{ data: T }> {
    try {
      const response = await axios.post(`${this.baseURL}${path}`, data, {
        headers: this.getHeaders(),
      });
      return { data: response.data };
    } catch (error) {
      handleAPIError(error);
      throw error;
    }
  }
}

export const apiClient = new APIClient();"}

{"explanation": "Atualizando o hook SSE para usar a URL base correta e melhorar o tratamento de erros", "filePath": "/home/monteiro/codes/vitaport_front/hooks/use-sse-health-data.ts", "code": ""use client"

import { useEffect, useRef, useState, useCallback } from "react"
import { toast } from "sonner"

export interface HealthDataUpdate {
  type: "connected" | "health-update" | "error"
  employeeId?: string
  bpm?: number
  spo2?: number
  stressLevel?: number
  skinTemp?: number
  timestamp?: string
  message?: string
}

export interface SSEConnectionState {
  isConnected: boolean
  isConnecting: boolean
  error: string | null
  lastUpdate: string | null
}

export function useSSEHealthData() {
  const [healthData, setHealthData] = useState<Map<string, HealthDataUpdate>>(new Map())
  const [connectionState, setConnectionState] = useState<SSEConnectionState>({
    isConnected: false,
    isConnecting: true,
    error: null,
    lastUpdate: null,
  })
  const eventSourceRef = useRef<EventSource | null>(null)

  const connect = useCallback(() => {
    if (eventSourceRef.current) {
      eventSourceRef.current.close()
    }

    setConnectionState((prev) => ({
      ...prev,
      isConnecting: true,
      error: null,
    }))

    const token = localStorage.getItem("token")
    if (!token) {
      setConnectionState((prev) => ({
        ...prev,
        isConnecting: false,
        error: "No authentication token found",
      }))
      return
    }

    try {
      const url = new URL("http://localhost:8081/healthdata/sse")
      url.searchParams.append("employeeId", "all")

      eventSourceRef.current = new EventSource(url.toString(), {
        withCredentials: true,
      })

      eventSourceRef.current.onopen = () => {
        setConnectionState((prev) => ({
          ...prev,
          isConnected: true,
          isConnecting: false,
          error: null,
        }))
        toast.success("Connected to health data stream")
      }

      eventSourceRef.current.onmessage = (event) => {
        try {
          const data: HealthDataUpdate = JSON.parse(event.data)
          if (data.type === "health-update" && data.employeeId) {
            setHealthData((prev) => new Map(prev.set(data.employeeId!, data)))
            setConnectionState((prev) => ({
              ...prev,
              lastUpdate: new Date().toISOString(),
            }))
          }
        } catch (error) {
          console.error("Error parsing SSE message:", error)
        }
      }

      eventSourceRef.current.onerror = (error) => {
        console.error("SSE connection error: ", error)
        setConnectionState((prev) => ({
          ...prev,
          isConnected: false,
          isConnecting: false,
          error: "Connection error",
        }))
        toast.error("Lost connection to health data stream. Reconnecting...")

        // Tentar reconectar após 5 segundos
        setTimeout(connect, 5000)
      }
    } catch (error) {
      console.error("Error creating SSE connection:", error)
      setConnectionState((prev) => ({
        ...prev,
        isConnecting: false,
        error: "Failed to create connection",
      }))
    }
  }, [])

  useEffect(() => {
    connect()
    return () => {
      if (eventSourceRef.current) {
        eventSourceRef.current.close()
      }
    }
  }, [connect])

  const disconnect = useCallback(() => {
    if (eventSourceRef.current) {
      eventSourceRef.current.close()
      eventSourceRef.current = null
    }
    setConnectionState((prev) => ({
      ...prev,
      isConnected: false,
      isConnecting: false,
    }))
  }, [])

