  PaginationPrevious,
} from "@/components/ui/pagination";
import { useRouter } from "next/navigation";

export default function EmployeesPage() {
  const router = useRouter();
  const { user } = useAuth();
  const { getEmployeesPage } = useEmployees();
  const [employees, setEmployees] = useState<ReadEmployeeDTO[]>([]);
  const [currentPage, setCurrentPage] = useState(0);
  const [totalPages, setTotalPages] = useState(0);
  const [isLoading, setIsLoading] = useState(true);
  const [error, setError] = useState<string | null>(null);
  const pageSize = 9;

  const fetchEmployees = useCallback(async (page: number) => {
    try {
      setIsLoading(true);
      setError(null);
      const response = await getEmployeesPage(page, pageSize);
      if (response) {
        setEmployees(response.content);
        setTotalPages(response.totalPages);
        setCurrentPage(response.number);
      } else {
        setError("Failed to load employees");
      }
    } catch (error) {
      console.error("Error fetching employees:", error);
      setError("An error occurred while loading employees");
    } finally {
      setIsLoading(false);
    }
  }, [getEmployeesPage]);

  useEffect(() => {
    // Verificar se o usuário está autenticado
    if (!user) {
      router.push('/login');
      return;
    }
    fetchEmployees(currentPage);
  }, [currentPage, fetchEmployees, user, router]);

  const handlePrevious = useCallback((e: React.MouseEvent) => {
    e.preventDefault();
    if (currentPage > 0) {
      setCurrentPage(p => p - 1);
    }
  }, [currentPage]);

  const handleNext = useCallback((e: React.MouseEvent) => {
    e.preventDefault();
    if (currentPage < totalPages - 1) {
      setCurrentPage(p => p + 1);
    }
  }, [currentPage, totalPages]);

  const handlePageClick = useCallback((e: React.MouseEvent, page: number) => {
    e.preventDefault();
    setCurrentPage(page);
  }, []);

  if (isLoading) {
    return (
      <div className="p-6">
        <div className="flex justify-center items-center min-h-[400px]">
          <div className="animate-pulse">Loading employees...</div>
        </div>
      </div>
    );
  }

  if (error) {
    return (
      <div className="p-6">
        <div className="flex flex-col items-center justify-center min-h-[400px] space-y-4">
          <p className="text-red-500">{error}</p>
          <Button onClick={() => fetchEmployees(currentPage)}>
            Try Again
          </Button>
        </div>
      </div>
    );
  }

  return (
    <div className="space-y-6 p-6">
      <div className="flex justify-between items-center">
        <h1 className="text-3xl font-bold">Employees</h1>
        {user?.userRole === "SYSTEM_MANAGER" && (
          <Link href="/dashboard/employees/new">
            <Button>Add Employee</Button>
          </Link>
        )}
      </div>

      {employees.length === 0 ? (
        <Card className="p-6">
          <div className="text-center">No employees found</div>
        </Card>
      ) : (
        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
          {employees.map((employee) => (
            <Link
              key={employee.id}
              href={`/dashboard/employees/${employee.id}`}
            >
              <Card className="p-6 hover:shadow-lg transition-shadow cursor-pointer">
                <div className="space-y-4">
                  <div>
                    <p className="text-xl font-semibold">{employee.fullName}</p>
                    <p className="text-sm text-gray-500">{employee.registrationNumber}</p>
                  </div>
                  <div>
                    <p className="text-sm text-gray-500">Email</p>
                    <p>{employee.user.email}</p>
                  </div>
                  <div>
                    <p className="text-sm text-gray-500">Role</p>
                    <p>{employee.user.userRole}</p>
                  </div>
                </div>
              </Card>
            </Link>
          ))}
        </div>
      )}

      {totalPages > 1 && (
        <Pagination>
          <PaginationContent>
            <PaginationItem>
              <PaginationPrevious 
                href="#"
                onClick={handlePrevious}
                aria-disabled={currentPage === 0}
                className={currentPage === 0 ? 'pointer-events-none opacity-50' : ''}
              />
            </PaginationItem>
            {[...Array(totalPages)].map((_, i) => (
              <PaginationItem key={i}>
                <PaginationLink
                  href="#"
                  onClick={(e) => handlePageClick(e, i)}
                  isActive={currentPage === i}
                >
                  {i + 1}
                </PaginationLink>
              </PaginationItem>
            ))}
            <PaginationItem>
              <PaginationNext
                href="#"
                onClick={handleNext}
                aria-disabled={currentPage === totalPages - 1}
                className={currentPage === totalPages - 1 ? 'pointer-events-none opacity-50' : ''}
              />
            </PaginationItem>
          </PaginationContent>
        </Pagination>
      )}
    </div>
  );
}"}

{"explanation": "Atualizando o api-client para melhorar o gerenciamento do token e adicionar interceptors do Axios", "filePath": "/home/monteiro/codes/vitaport_front/lib/api-client.ts", "code": "import axios, { AxiosInstance } from "axios";
import { toast } from "sonner";

// API Base Configuration
const API_BASE_URL = "http://localhost:8081";

export const handleAPIError = (error: any) => {
  console.error("API Error: ", error);
  const message = error.response?.data?.message || error.message || "An error occurred";
  toast.error(message);
  if (error.response?.status === 401 || error.response?.status === 403) {
    localStorage.removeItem("token");
    window.location.href = "/login";
  }
};

export class APIClient {
  private axiosInstance: AxiosInstance;

  constructor() {
    this.axiosInstance = axios.create({
      baseURL: API_BASE_URL,
      headers: {
        "Content-Type": "application/json",
      },
    });

    // Request interceptor
    this.axiosInstance.interceptors.request.use(
      (config) => {
        const token = localStorage.getItem("token");
        if (token) {
          config.headers["Authorization"] = `Bearer ${token}`;
        }
        return config;
      },
      (error) => {
        return Promise.reject(error);
      }
    );

    // Response interceptor
    this.axiosInstance.interceptors.response.use(
      (response) => response,
      (error) => {
        handleAPIError(error);
        return Promise.reject(error);
      }
    );
  }

  setToken(token: string) {
    if (typeof window !== "undefined") {
      localStorage.setItem("token", token);
    }
  }

  clearToken() {
    if (typeof window !== "undefined") {
      localStorage.removeItem("token");
    }
  }

  async get<T>(path: string): Promise<{ data: T }> {
    try {
      const response = await this.axiosInstance.get<T>(path);
      return { data: response.data };
    } catch (error) {
      // O interceptor já trata o erro
      throw error;
    }
  }

  async post<T>(path: string, data: any): Promise<{ data: T }> {
    try {
      const response = await this.axiosInstance.post<T>(path, data);
      return { data: response.data };
    } catch (error) {
      // O interceptor já trata o erro
      throw error;
    }
  }
}

export const apiClient = new APIClient();"}

