const [retryCount, setRetryCount] = useState(0)

const connect = useCallback(() => {
  if (eventSourceRef.current) {
    eventSourceRef.current.close()
  }

  if (retryCount >= SSE_MAX_RETRIES) {
    setConnectionState((prev) => ({
      ...prev,
      isConnecting: false,
      error: "Maximum retry attempts reached. Please refresh the page.",
    }))
    toast.error("Connection failed after multiple attempts")
    return
  }

  setConnectionState((prev) => ({
    ...prev,
    isConnecting: true,
    error: null,
  }))

  const token = localStorage.getItem("token")
  if (!token) {
    setConnectionState((prev) => ({
      ...prev,
      isConnecting: false,
      error: "No authentication token found",
    }))
    return
  }

  try {
    const url = new URL(`${SSE_URL}/healthdata/sse`)
    url.searchParams.append("employeeId", "all")

    const eventSource = new EventSourcePolyfill(url.toString(), {
      headers: {
        Authorization: `Bearer ${token}`,
      },
      withCredentials: true,
      heartbeatTimeout: 30000,
    })

eventSource.onerror = (error) => {
  console.error("SSE connection error: ", error)
  setConnectionState((prev) => ({
    ...prev,
    isConnected: false,
    isConnecting: false,
    error: "Connection error",
  }))
  eventSource.close()

  // Clear previous timeout if exists
  if (reconnectTimeoutRef.current) {
    clearTimeout(reconnectTimeoutRef.current)
  }

  setRetryCount((prev) => prev + 1)

  // Attempt to reconnect after SSE_RETRY_INTERVAL
  reconnectTimeoutRef.current = setTimeout(() => {
    toast.info("Attempting to reconnect...")
    connect()
  }, SSE_RETRY_INTERVAL)

useEffect(() => {
  connect()
  return () => {
    if (reconnectTimeoutRef.current) {
      clearTimeout(reconnectTimeoutRef.current)
    }
    if (eventSourceRef.current) {
      eventSourceRef.current.close()
    }
    setRetryCount(0)
  }
}, [connect])

