        },
        withCredentials: true,
        heartbeatTimeout: 30000,
      })

      eventSource.onopen = () => {
        setConnectionState((prev) => ({
          ...prev,
          isConnected: true,
          isConnecting: false,
          error: null,
        }))
        toast.success("Connected to health data stream")
      }

      eventSource.onmessage = (event) => {
