          const data: HealthDataUpdate = JSON.parse(event.data)
          if (data.type === "health-update" && data.employeeId) {
            setHealthData((prev) => new Map(prev.set(data.employeeId!, data)))
            setConnectionState((prev) => ({
              ...prev,
