      // Salvar token em cookie seguro
      Cookies.set('token', tokenJWT, {
        expires: 7, // expira em 7 dias
        secure: process.env.NODE_ENV === 'production',
        sameSite: 'strict'
      });

      // Buscar dados do usuário
      const userData = await apiClient.getUserData(decoded.sub);
      setUser(userData);

      toast.success("Login realizado com sucesso!");
      router.push("/dashboard");
    } catch (error: any) {
      Cookies.remove('token');
      setUser(null);
      toast.error(error.message || "Erro ao fazer login. Verifique suas credenciais.");
      throw error;
    } finally {
      setIsLoading(false);
    }
  };

  const register = async (data: RegisterUserDTO) => {
    try {
      setIsLoading(true);
      await apiClient.register(data);
      toast.success("Registro realizado com sucesso! Faça login para continuar.");
      router.push("/login");
    } catch (error: any) {
      toast.error(error.message || "Erro ao registrar. Tente novamente.");
      throw error;
    } finally {
      setIsLoading(false);
    }
  };

  const logout = () => {
    Cookies.remove('token');
    setUser(null);
    router.push("/login");
    toast.success("Logout realizado com sucesso!");
  };

  return (
    <AuthContext.Provider value={{ user, login, register, logout, isLoading }}>
      {children}
    </AuthContext.Provider>
  );
}

export function useAuth() {
  const context = useContext(AuthContext);
  if (!context) {
    throw new Error("useAuth deve ser usado dentro de um AuthProvider");
  }
  return context;
}"}

