import { useEffect, useRef, useState, useCallback } from "react";
import { HealthData, SSEHealthDataEvent } from "@/types/health-data";

export interface SSEConnectionState {
  isConnected: boolean;
  isConnecting: boolean;
  error: string | null;
  lastUpdate: string | null;
}

export function useSSEHealthData(employeeId = "all", token?: string) {
  const [healthData, setHealthData] = useState<Map<string, HealthData>>(new Map());
  const [connectionState, setConnectionState] = useState<SSEConnectionState>({
    isConnected: false,
    isConnecting: false,
    error: null,
    lastUpdate: null,
  });

  const eventSourceRef = useRef<EventSource | null>(null);
  const reconnectTimeoutRef = useRef<NodeJS.Timeout | null>(null);
  const reconnectAttempts = useRef(0);
  const maxReconnectAttempts = 5;

  const connect = useCallback(() => {
    if (eventSourceRef.current?.readyState === EventSource.OPEN) {
      return; // Already connected
    }

    setConnectionState((prev) => ({ ...prev, isConnecting: true, error: null }));

    try {
      const url = `/healthdata/sse?employeeId=${encodeURIComponent(employeeId)}`;

      // Create EventSource with authorization header
      const eventSource = new EventSource(url, {
        withCredentials: true,
      });

      // Add authorization header to the request
      if (token) {
        const originalOpen = XMLHttpRequest.prototype.open;
        XMLHttpRequest.prototype.open = function(this: XMLHttpRequest) {
          originalOpen.apply(this, arguments as any);
          this.setRequestHeader("Authorization", `Bearer ${token}`);
        };
      }

      eventSourceRef.current = eventSource;

      eventSource.onopen = () => {
        setConnectionState({
          isConnected: true,
          isConnecting: false,
          error: null,
          lastUpdate: new Date().toISOString(),
        });
        reconnectAttempts.current = 0;
      };

      eventSource.onmessage = (event) => {
        try {
          const sseEvent: SSEHealthDataEvent = JSON.parse(event.data);

          if (sseEvent.type === "health-update" && sseEvent.data) {
            setHealthData((prev) => {
              const newMap = new Map(prev);
              newMap.set(sseEvent.data!.employee_id, sseEvent.data!);
              return newMap;
            });

            setConnectionState((prev) => ({
              ...prev,
              lastUpdate: sseEvent.data.timestamp,
            }));
          }
        } catch (error) {
          console.error("Error parsing SSE data:", error);
        }
      };

      eventSource.onerror = (error) => {
        console.error("SSE connection error:", error);
        setConnectionState((prev) => ({
          ...prev,
          isConnected: false,
          isConnecting: false,
          error: "Connection lost. Attempting to reconnect...",
        }));

        eventSource.close();

        // Implement exponential backoff for reconnection
        if (reconnectAttempts.current < maxReconnectAttempts) {
          const delay = Math.min(1000 * Math.pow(2, reconnectAttempts.current), 30000);
          reconnectAttempts.current++;

          reconnectTimeoutRef.current = setTimeout(() => {
            connect();
          }, delay);
        } else {
          setConnectionState((prev) => ({
            ...prev,
            error: "Maximum reconnection attempts reached. Please refresh the page.",
          }));
        }
      };

    } catch (error) {
      console.error("Error setting up SSE connection:", error);
      setConnectionState((prev) => ({
        ...prev,
        isConnecting: false,
        error: "Failed to establish connection.",
      }));
    }
  }, [employeeId, token]);

  useEffect(() => {
    connect();

    return () => {
      if (eventSourceRef.current) {
        eventSourceRef.current.close();
      }
      if (reconnectTimeoutRef.current) {
        clearTimeout(reconnectTimeoutRef.current);
      }
    };
  }, [connect]);

  return {
    healthData,
    connectionState,
  };
